$(document).ready((function () {
    $("#lastNewsContainer > ul").innerfade({
        timeout: 5e3
    }), $(".accordion > .card > .card-header").click((function () {
        $(this).find(".accordion-header-icon").toggleClass("fa-chevron-down fa-chevron-up", (function () {
            $(this).toggleCollapse()
        }))
    }))
}));

$(document).on("change", "#newProfilePhoto", function () {
    var triggerInput = this;
    var currentImg = $(this).closest(".pic-holder").find(".pic").attr("src");
    var holder = $(this).closest(".pic-holder");
    var wrapper = $(this).closest(".profile-pic-wrapper");
    $(wrapper).find('[role="alert"]').remove();
    var files = !!this.files ? this.files : [];
    if (!files.length || !window.FileReader) {
        return;
    }
    if (/^image/.test(files[0].type)) {
        // only image file
        var reader = new FileReader(); // instance of the FileReader
        reader.readAsDataURL(files[0]); // read the local file

        reader.onloadend = function () {
            $(holder).addClass("uploadInProgress");
            $(holder).find(".pic").attr("src", this.result);
            $(holder).append(
                '<div class="upload-loader"><div class="spinner-border text-primary" role="status"><span class="sr-only">Loading...</span></div></div>'
            );

            // Dummy timeout; call API or AJAX below
            setTimeout(() => {
                $(holder).removeClass("uploadInProgress");
                $(holder).find(".upload-loader").remove();
                // If upload successful
                //if (Math.random() < 0.9) {
                //    $(wrapper).append(
                //        '<div class="snackbar show" role="alert"><i class="fa fa-check-circle text-success"></i> تصویر با موفقیت بارگزاری شد</div>'
                //    );

                //    // Clear input after upload
                //    $(triggerInput).val("");

                //    setTimeout(() => {
                //        $(wrapper).find('[role="alert"]').remove();
                //    }, 3000);
                //}
                //else {
                //    $(holder).find(".pic").attr("src", currentImg);
                //    $(wrapper).append(
                //        '<div class="snackbar show" role="alert"><i class="fa fa-times-circle text-danger"></i> خطایی هنگام بارگزاری بوجود آمد! لطفاً دوباره تلاش کنید</div>'
                //    );

                //    // Clear input after upload
                //    $(triggerInput).val("");
                //    setTimeout(() => {
                //        $(wrapper).find('[role="alert"]').remove();
                //    }, 3000);
                //}
            }, 1500);
        };
    } else {
        $(wrapper).append(
            '<div class="alert alert-danger d-inline-block p-2 small" role="alert"> لطفاً یک عکس با فرمت صحیح بارگزاری کنید.</div>'
        );
        setTimeout(() => {
            $(wrapper).find('role="alert"').remove();
        }, 3000);
    }
});
